import React, { useRef, useEffect } from 'react';

const ImageDownload = () => {
  const imageRef = useRef(null);
  const imageUrl = 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTl-zgRupZ1NINt4VAaO2fem64Z9SofTovAag&usqp=CAU'; // Replace with the actual image URL

  useEffect(() => {
    // Fetch the image and convert it to Base64 data URL
    fetch(imageUrl)
      .then((response) => response.blob())
      .then((blob) => {
        const reader = new FileReader();
        reader.onloadend = () => {
          // Set the Base64 data URL as the image source
          imageRef.current.src = reader.result;
        };
        reader.readAsDataURL(blob);
      })
      .catch((error) => console.error('Error fetching the image:', error));
  }, [imageUrl]);

  // Function to download the image with different resolutions and orientations
  const downloadImages = () => {
    const imageElement = imageRef.current;

    // Define the different resolutions and orientations
    const images = [
      { width: 1024, height: 768, orientation: 'landscape' },
    //   { width: 768, height: 1024, orientation: 'portrait' },
    //   { width: 800, height: 800, orientation: 'mixed' },
      //   Add more resolutions and orientations as needed
    ];

    // Loop through each configuration and download the corresponding image
    images.forEach(({ width, height, orientation }) => {
      // Create a canvas element to adjust the resolution
      const canvas = document.createElement('canvas');
      canvas.width = width;
      canvas.height = height;
      const context = canvas.getContext('2d');

      // Adjust the orientation if needed
      if (orientation === 'portrait') {
        context.save();
        context.translate(width / 2, height / 2);
        context.rotate(-Math.PI / 2);
        context.drawImage(imageElement, -height / 2, -width / 2, height, width);
        context.restore();
      } else {
        context.drawImage(imageElement, 0, 0, width, height);
      }

      // Convert the canvas to a data URL and open it in a new tab for download
      canvas.toBlob((blob) => {
        const url = URL.createObjectURL(blob);
        const anchor = document.createElement('a');
        anchor.href = url;
        anchor.download = `image_${width}x${height}_${orientation}.png`;

        // Open the download link in a new tab
        const newTab = window.open('', '_blank');
        newTab.location.href = anchor.href;

        // Clean up the object URL
        URL.revokeObjectURL(url);
      }, 'image/png');
    });
  };

  return (
    <div>
      {/* The image will be updated with the Base64 data URL once fetched */}
      <img ref={imageRef} alt="Image" />

      {/* Download button for all resolutions and orientations */}
      <button onClick={downloadImages}>Download All Images</button>
    </div>
  );
};

export default ImageDownload;
