// StripeCheckoutButton.js
import React, { useEffect } from "react";
import { useNavigate } from "react-router-dom";

const StripeCheckout = ({ publicKey, userId }) => {
  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `/create-checkout-session?userid=${userId}`
        );
        const data = await response.json();
        console.log(data);
        redirectToStripeCheckout(data.sessionId);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    const redirectToStripeCheckout = (sessionId) => {
      const stripeScript = document.createElement("script");
      stripeScript.src = "https://js.stripe.com/v3/";
      stripeScript.async = true;
      stripeScript.onload = () => {
        const stripe = window.Stripe(publicKey);
        stripe
          .redirectToCheckout({ sessionId })
          .then((result) => {
            // Handle any errors during the redirection if needed
            console.log("Redirect result:", result);

            // Check if the payment was successful and redirect to the success page
            if (result.error) {
              console.error("Payment error:", result.error.message);
            } else if (
              result.payment_intent &&
              result.payment_intent.status === "succeeded"
            ) {
              history.push("/success");
            }
          })
          .catch((error) => {
            console.error("Error redirecting to Stripe Checkout:", error);
          });
      };

      document.body.appendChild(stripeScript);
    };

    fetchData();
  }, [publicKey, userId, history]);

  return <></>; // You can return null or an empty fragment since this component doesn't render anything
};

export default StripeCheckout;
