import React, { useRef } from 'react';

const ImageDownload = () => {
  const imageRef = useRef(null);

  // Function to download the image
  const downloadImage = () => {
    // Replace 'http://localhost:3000/download-image' with your server's endpoint
    const imageUrl = 'http://localhost:3000/download-image';

    // Create a temporary anchor element to trigger the download
    const anchor = document.createElement('a');
    anchor.href = imageUrl;
    anchor.download = 'image_1024.png';
    anchor.click();
  };

  return (
    <div>
      {/* Replace 'your_image_url.jpg' with the actual image URL */}
      <img ref={imageRef} src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTl-zgRupZ1NINt4VAaO2fem64Z9SofTovAag&usqp=CAU" alt="Image" />

      <button onClick={downloadImage}>Download Image (1024x1024)</button>
    </div>
  );
};

export default ImageDownload;
